import React, { useState } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLightTheme, setIsLightTheme] = useState(false); // Set default to dark theme

  // Function to open modal
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Toggle theme
  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  return (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      {/* Navbar */}
      <nav className="navbar">
        <div className="workspace-name">Dewank Rastogi's workspace</div>
        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={!isLightTheme}  // Invert logic: checkbox is checked when light theme is active
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-button">Share</button>
      </nav>

      {/* Dashboard Content */}
      <div className="dashboard-content">
        <button className="create-folder" onClick={openModal}>
          <img src="folder.png" className="folder" alt="Folder" />
          Create a folder
        </button>
       <button><div className="create-typebot">
          <div className="typebot-card">
            <span className="plus">+</span>
            <p>Create a typebot</p>
          </div>
        </div></button> 
      </div>

      {/* Conditional rendering of modal with passed theme */}
      {isModalOpen && <CreateFolderModal closeModal={closeModal} isLightTheme={isLightTheme} />}
    </div>
  );
};

export default Dashboard;
