import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = ({ onBackClick, onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Handle Back Button Click
  const handleBackClick = () => {
    if (onBackClick) onBackClick();
  };

  // Handle Register Button Click
  const handleRegisterClick = () => {
    navigate('/signup');
  };

  // Handle Form Submit
  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Store the token securely
        localStorage.setItem('authToken', data.token);

        // Optionally, you can store other user details if needed
        localStorage.setItem('user', JSON.stringify(data.user));

        // On successful login, redirect or notify parent component
        alert('Login Successful!');
        if (onLoginSuccess) onLoginSuccess();
        navigate('/dashboard'); // Redirect to dashboard or home page
      } else {
        setError(data.message || 'Invalid login credentials');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <button className="back-button" onClick={handleBackClick}>
        <img src="arrow_back.png" alt="Back" />
      </button>

      <div className="login-container">
        <form onSubmit={handleLoginSubmit}>
          <label>Email</label>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label>Password</label>
          <input
            type="password"
            placeholder="********"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {error && <div className="error">{error}</div>}

          <button type="submit" className="btn login-btn">
            Log In
          </button>

          <div className="or">OR</div>

          <button type="button" className="btn google-btn">
            <img src="google.png" alt="Google Icon" />
            Sign In with Google
          </button>
        </form>
        <div className="register">
          Donâ€™t have an account?{" "}
          <button onClick={handleRegisterClick} className="register-btn">
            Register now
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
