import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import MainPage from './Components/Main-page/Mainpage';
import LoginPage from './Components/Login-page/LoginPage';
import Sign from './Components/Sign-page/Sign'; // Import Sign (Signup) page
import Dashboard from './Components/Dashboard/Dashboard'; // Import Dashboard component

function App() {
  return (
    <div className="App">
      <Router>
        <Switch>
          {/* Route for the MainPage */}
          <Route exact path="/" component={MainPage} />

          {/* Route for the LoginPage */}
          <Route 
            path="/login" 
            render={(props) => <LoginPage {...props} />} 
          />

          {/* Route for the SignUp page */}
          <Route 
            path="/signup" 
            render={(props) => <Sign {...props} />} 
          />

          {/* Route for the Dashboard */}
          <Route 
            path="/dashboard" 
            render={(props) => <Dashboard {...props} />} 
          />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
