import React, { useState } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings"; // Import Settings component

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [folders, setFolders] = useState([]);
  const [forms, setForms] = useState([]);
  const [deleteFolderIndex, setDeleteFolderIndex] = useState(null);
  const [deleteFormIndex, setDeleteFormIndex] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isDeleteFormModalOpen, setIsDeleteFormModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false); // State to toggle Settings

  // Function to toggle theme
  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  // Function to toggle dropdown menu
  const toggleDropdown = () => {
    setIsDropdownOpen((prevState) => !prevState);
  };

  // Function to open Settings
  const openSettings = () => {
    setIsSettingsOpen(true); // Set the state to show the Settings component
  };

  // Function to go back to Dashboard
  const closeSettings = () => {
    setIsSettingsOpen(false); // Set the state to show the Dashboard
  };

  return isSettingsOpen ? (
    <Settings closeSettings={closeSettings} /> // Render Settings if open
  ) : (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      {/* Navbar */}
      <nav className="navbar">
        <div className="workspace-container">
          <button className="workspace-button" onClick={toggleDropdown}>
            <span>Dewank Rastogi's workspace</span>
            <img className="dropdown-icon" src="drop.png" alt="Dropdown Icon" />
          </button>

          {/* Dropdown menu */}
          {isDropdownOpen && (
            <div className="dropdown-menu">
              <button className="dropdown-item" onClick={openSettings}>
                Settings
              </button>
              <button className="dropdown-item logout">Log Out</button>
            </div>
          )}
        </div>

        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={!isLightTheme}
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-button">Share</button>
      </nav>

      {/* Rest of the Dashboard Code */}
      {/* Content like creating folders/forms */}
    </div>
  );
};

export default Dashboard;
