import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import MainPage from './Components/Main-page/Mainpage';
import LoginPage from './Components/Login-page/LoginPage';
import Sign from './Components/Sign-page/Sign';
import Dashboard from './Components/Dashboard-page/Dashboard'; // Import Dashboard page

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Track authentication status

  const handleLogin = () => {
    setIsAuthenticated(true); // Set user as authenticated
  };

  const handleLogout = () => {
    setIsAuthenticated(false); // Set user as logged out
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<MainPage />} />
        <Route
          path="/login"
          element={<LoginPage onLogin={handleLogin} />} // Pass login handler to LoginPage
        />
        <Route path="/signup" element={<Sign onNavigateToLogin={() => {}} />} />
        <Route
          path="/dashboard"
          element={isAuthenticated ? <Dashboard /> : <LoginPage onLogin={handleLogin} />} // Redirect to Login if not authenticated
        />
      </Routes>
    </Router>
  );
}

export default App;
