import React, { useState } from "react";
import "./ShareModal.css";

const ShareModal = ({ isOpen, onClose }) => {
  const [email, setEmail] = useState("");
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [mode, setMode] = useState("edit"); // "edit" or "view"

  const handleEmailChange = (e) => setEmail(e.target.value);

  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

  const handleSendInvite = () => {
    if (email) {
      alert(`Invite sent to ${email}`);
    } else {
      alert("Please enter a valid email.");
    }
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText("https://example.com/invite");
    alert("Link copied to clipboard!");
  };

  const toggleMode = (newMode) => {
    setMode(newMode);
    setDropdownOpen(false); // Close the dropdown after selecting
  };

  if (!isOpen) return null;

  return (
    <div className="share-modal-overlay">
      <div className="share-modal">
        <div className="share-modal-header">
          <button className="close-button" onClick={onClose}>
            <img src="close.png" alt="Close" />
          </button>
        </div>
        <h3 className="share-modal-title">Invite by Email</h3>
        <div className="email-input-container">
          <div className="dropdown" onClick={toggleDropdown}>
            {mode === "edit" ? "Edit ▼" : "View ▼"} {/* Toggle between "Edit" and "View" */}
          </div>
          {dropdownOpen && (
            <div className="dropdown-menu-share">
              <div
                className="dropdown-item-share-edit"
                onClick={() => toggleMode("edit")}
              >
                Edit
              </div>
              <div
                className="dropdown-item-share-view"
                onClick={() => toggleMode("view")}
              >
                View
              </div>
            </div>
          )}
        </div>

        {mode === "edit" ? (
          <input
            type="email"
            placeholder="Enter email id"
            value={email}
            onChange={handleEmailChange}
            className="email-input"
          />
        ) : (
          <p>{email || "No email entered"}</p> // Display the entered email or a message in "View" mode
        )}

        <button className="send-invite-button" onClick={handleSendInvite}>
          Send Invite
        </button>

        <h3 className="share-modal-title">Invite by link</h3>
        <button className="copy-link-button" onClick={handleCopyLink}>
          Copy link
        </button>
      </div>
    </div>
  );
};

export default ShareModal;
