import React, { useState } from "react";
import "./Settings.css";

const Settings = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showOldPassword, setShowOldPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);

  const handleUpdate = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("No authentication token found. Please log in.");
      window.location.href = "/login";
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ username, email, oldPassword, newPassword }),
      });
      const data = await response.json();
      if (response.ok) {
        alert("Update successful");
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error updating user:", error);
      alert("Something went wrong. Please try again.");
    }
  };

  return (
    <div className="container">
      <h1 className="title">Settings</h1>
      <div className="form">
        <div className="inputContainer">
          <img src="user.png" alt="User Icon" className="icon" />
          <input
            type="text"
            placeholder="Name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input"
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Email Icon" className="icon" />
          <input
            type="email"
            placeholder="Update Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input"
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showOldPassword ? "text" : "password"}
            placeholder="Old Password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowOldPassword(!showOldPassword)}
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showNewPassword ? "text" : "password"}
            placeholder="New Password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowNewPassword(!showNewPassword)}
          />
        </div>

        <button className="update-button" onClick={handleUpdate}>
          Update
        </button>
      </div>

      <div className="logoutContainer">
        <img src="logout.png" alt="Logout Icon" className="logoutIcon" />
        <button
          className="logoutButton"
          onClick={() => {
            localStorage.removeItem("token");
            window.location.href = "/login";
          }}
        >
          Log out
        </button>
      </div>
    </div>
  );
};

export default Settings;
