import React, { useState } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Function to open modal
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="dashboard-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="workspace-name">Dewank Rastogi's workspace</div>
        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input type="checkbox" />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-button">Share</button>
      </nav>

      {/* Dashboard Content */}
      <div className="dashboard-content">
        <button className="create-folder" onClick={openModal}>
          <img src="folder.png" className="folder" alt="Folder" />
          Create a folder
        </button>
        <div className="create-typebot">
          <div className="typebot-card">
            <span className="plus">+</span>
            <p>Create a typebot</p>
          </div>
        </div>
      </div>

      {/* Conditional rendering of modal */}
      {isModalOpen && <CreateFolderModal closeModal={closeModal} />}
    </div>
  );
};

export default Dashboard;
