import React, { useState } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // State for dropdown visibility

  // Function to open modal with specified type
  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  // Function to close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  // Toggle theme
  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  // Toggle dropdown menu
  const toggleDropdown = () => {
    setIsDropdownOpen((prevState) => !prevState);
  };

  return (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      {/* Navbar */}
      <nav className="navbar">
        
        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={!isLightTheme}
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-button">Share</button>
      </nav>

      {/* Dashboard Content */}
      <div className="dashboard-content">
        <button className="create-folder" onClick={() => openModal("folder")}>
          <img src="folder.png" className="folder" alt="Folder" />
          Create a folder
        </button>
        <button className="create-typebot" onClick={() => openModal("form")}>
          <p>
            <span className="plus">+</span> <br />
            Create a typebot
          </p>
        </button>
      </div>

      {/* Conditional rendering of modals */}
      {isModalOpen && modalType === "folder" && (
        <CreateFolderModal closeModal={closeModal} isLightTheme={isLightTheme} />
      )}
      {isModalOpen && modalType === "form" && (
        <CreateFormModal closeModal={closeModal} isLightTheme={isLightTheme} />
      )}
    </div>
  );
};

export default Dashboard;
