import FolderForms from "./FolderForms";
import React from "react";

const Folders = ({ folders, activeFolderIndex, toggleFolderVisibility, openDeleteFolderModal }) => {
  return (
    <ul className="folder-list">
      {folders.map((folder, index) => (
        <li key={index}>
          <div className="folder-item">
            <span onClick={() => toggleFolderVisibility(index)}>{folder.name}</span>
            <button
              className="delete-button"
              onClick={() => openDeleteFolderModal(index)}
            >
              <img src="del.png" alt="Delete" />
            </button>
            {activeFolderIndex === index && (
              <FolderForms folder={folder} openDeleteFormModal={openDeleteFolderModal} />
            )}
          </div>
        </li>
      ))}
    </ul>
  );
};

export default Folders;
