import React from 'react'

function FlowTextInput() {
  return (
    <div className='Flow-Inputs'>
        <input type="text" placeholder="Enter your text" className='FlowTextInput' /> 
        <button className='FlowTextInputButton'><img src='send.png'></img></button>
    </div>
  )
}

export default FlowTextInput
