import React, { useState } from "react";
import "./Settings.css";

const Settings = ({ closeSettings }) => {
  const [showOldPassword, setShowOldPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);

  return (
    <div className="container">
      <h1 className="title">Settings</h1>
      <div className="form">
        {/* Name Input */}
        <div className="inputContainer">
          <img src="user.png" alt="User Icon" className="icon" />
          <input type="text" placeholder="Name" className="input" />
        </div>

        {/* Email Input */}
        <div className="inputContainer">
          <img src="lock.png" alt="Email Icon" className="icon" />
          <input type="email" placeholder="Update Email" className="input" />
        </div>

        {/* Old Password Input */}
        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showOldPassword ? "text" : "password"}
            placeholder="Old Password"
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowOldPassword(!showOldPassword)}
          />
        </div>

        {/* New Password Input */}
        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showNewPassword ? "text" : "password"}
            placeholder="New Password"
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowNewPassword(!showNewPassword)}
          />
        </div>

        {/* Update Button */}
        <button className="update-button">Update</button>
      </div>

      {/* Back to Dashboard */}
      <button className="back-button" onClick={closeSettings}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default Settings;
