import React, { useState } from "react";
import "./ShareModal.css"; // Add styles to match the design

const ShareModal = ({ onClose }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleOptionClick = (option) => {
    console.log(`${option} selected`);
    setDropdownOpen(false);
  };

  return (
    <div className="share-modal-overlay">
      <div className="share-modal">
        <div className="modal-header">
          <span>Invite by Email</span>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>

        <div className="email-invite">
          <input
            type="email"
            placeholder="Enter email id"
            className="email-input"
          />
          <button className="send-invite-button">Send Invite</button>
        </div>

        <div className="dropdown-container">
          <button className="dropdown-button" onClick={toggleDropdown}>
            Edit <span className="caret">&#9662;</span>
          </button>
          {dropdownOpen && (
            <div className="dropdown-menu">
              <div
                className="dropdown-item"
                onClick={() => handleOptionClick("Edit")}
              >
                Edit
              </div>
              <div
                className="dropdown-item"
                onClick={() => handleOptionClick("View")}
              >
                View
              </div>
            </div>
          )}
        </div>

        <div className="invite-link">
          <span>Invite by Link</span>
          <button className="copy-link-button">Copy link</button>
        </div>
      </div>
    </div>
  );
};

export default ShareModal;
