import React from 'react'
import { useState } from'react'
import './Flow/FlowForm.css'
function FlowForm() {


 
      const [selected, setSelected] = useState(null);
    
      const handleSelect = (index) => {
        setSelected(index);
      };
  return (
    <div>
        <form>
            <label>
                <input type="text" placeholder="Form Name" />
                <button type="submit"><img src='send.png'></img></button>
            </label>
            <label>
                <input type='email' placeholder="Form Name" />
                <button type="submit"><img src='send.png'></img></button>
            </label>
            <label>
                <input type="number" placeholder="Form Name" />
                <button type="submit"><img src='send.png'></img></button>
            </label>
            <label>
                <input type="number" placeholder="Form Name" />
                <button type="submit"><img src='send.png'></img></button>
            </label>
            
             <div className="flow-rating">
      {[1, 2, 3, 4, 5].map((item, index) => (
        <div
          key={index}
          className={`circle ${selected === index ? "selected" : ""}`}
          onClick={() => handleSelect(index)}
        >
          {item}
        </div> 
      ))} <button className="FlowRating"><img src="send.png"></img></button>
    </div>
        </form>
    </div>
  )
}

export default FlowForm
