import React, { useState } from "react";
import "./FormBot.css";  // Assuming a new CSS file for styling
import InputComp from "./Workflow-buttons/InputComp";
import ImageComp from "./Workflow-buttons/ImageComp";
import InputText from "./Workflow-buttons/InputText";
import InputNumber from "./Workflow-buttons/InputNumber";
import InputEmail from "./Workflow-buttons/InputEmail";
import InputDate from "./Workflow-buttons/InputDate";
import InputPhone from "./Workflow-buttons/InputPhone";
import InputButton from "./Workflow-buttons/InputButton";
import Response from "./Response";

const FormBot = () => {
  const [workflowItems, setWorkflowItems] = useState([]);
  const [isResponseVisible, setIsResponseVisible] = useState(false);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isShareVisible, setIsShareVisible] = useState(false); 

  const handleAddToWorkflow = (component) => {
    setWorkflowItems((prevItems) => [...prevItems, component]);
  };

  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  const showResponseComponent = () => {
    setIsResponseVisible(true);
  };

  const handleShareClick = () => {
    setIsShareVisible(true);
  };

  if (isResponseVisible) {
    return <Response />;
  }

  return (
    <div className={`formbot-container ${isLightTheme ? "light-theme" : "dark-theme"}`}>
      <nav className="formbot-nav">
        <div className="rename-section">
          <input type="text" className="rename-input" placeholder="Enter Form Name" />
        </div>

        <div className="button-group">
          <button className="flow-button active">Flow</button>
          <button className="response-button" onClick={showResponseComponent}>
            Response
          </button>
        </div>

        <div className="theme-share-save-buttons">
          <div className="theme-toggle">
            <span>Light</span>
            <label className="switch">
              <input
                type="checkbox"
                checked={isLightTheme}
                onChange={toggleTheme}
              />
              <span className="slider"></span>
            </label>
            <span>Dark</span>
          </div>
          <button className="share-button" onClick={handleShareClick}>Share</button>
          <button className="save-button">Save</button>
          <button className="close-button">
            <img src="close.png" alt="Close" />
          </button>
        </div>
      </nav>

      <div className="formbot-workspace">
        <div className="sidebar">
          <div className="section">
            <h3 className="section-title">Bubbles</h3>
            <div className="button-grid">
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputComp />)}
              >
                <img src="text.png" alt="Text" className="icon" /> Text
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<ImageComp />)}
              >
                <img src="img.png" alt="Image" className="icon" /> Image
              </button>
              <button className="tool-button">
                <img src="video.png" alt="Video" className="icon" /> Video
              </button>
              <button className="tool-button">
                <img src="gif.png" alt="GIF" className="icon" /> GIF
              </button>
            </div>
          </div>

          <div className="section">
            <h3 className="section-title">Inputs</h3>
            <div className="button-grid">
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputText />)}
              >
                <img src="t.png" alt="Text" className="icon" /> Text
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputNumber />)}
              >
                <img src="no.png" alt="Number" className="icon" /> Number
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputEmail />)}
              >
                <img src="mail.png" alt="Email" className="icon" /> Email
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputPhone />)}
              >
                <img src="ph.png" alt="Phone" className="icon" /> Phone
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputDate />)}
              >
                <img src="date.png" alt="Date" className="icon" /> Date
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputButton />)}
              >
                <img src="button.png" alt="Buttons" className="icon" /> Buttons
              </button>
            </div>
          </div>
        </div>

        <div className="main-panel">
          <ul className="workflow-list">
            <div className="start-button">
              <img src={isLightTheme ? "flag.png" : "flagb.png"} alt="Start" className="icon" /> Start
            </div>
            {workflowItems.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default FormBot;
