import React, { useState } from 'react';
import './Settings.css';


function Settings() {
  // State to toggle password visibility
  const [showPassword, setShowPassword] = useState({
    oldPassword: false,
    newPassword: false,
  });

  const togglePasswordVisibility = (field) => {
    setShowPassword((prevState) => ({
      ...prevState,
      [field]: !prevState[field],
    }));
  };

  return (
    <div className="settings-container">
      <div className="settings-form">
        <h2 className="settings-title">Settings</h2>

        {/* Name Field */}
        <div className="input-container">
          <img src='user.png'></img>
          <input  className="input-field" type="text" placeholder="Name" />
        </div>

        {/* Email Field */}
        <div className="input-container">
        <img src='lock.png'></img>
          <input className="input-field" type="email" placeholder="Update Email" />
        </div>

        {/* Old Password Field */}
        <div className="input-container">
        <img src='lock.png'></img>
          <input
            className="input-field"
            type={showPassword.oldPassword ? 'text' : 'password'}
            placeholder="Old Password"
          />
          <div
            className="icon right"
            onClick={() => togglePasswordVisibility('oldPassword')}
          >
            {showPassword.oldPassword ?  <img src='eye.png'></img> : <img src='eye.png'></img>}
          </div>
        </div>

        {/* New Password Field */}
        <div className="input-container">
        <img src='lock.png'></img>
          <input
            className="input-field"
            type={showPassword.newPassword ? 'text' : 'password'}
            placeholder="New Password"
          />
          <div
            className="icon right"
            onClick={() => togglePasswordVisibility('newPassword')}
          >
            {showPassword.newPassword ? <img src='eye.png'></img>  :<img src='eye.png'></img> }
          </div>
        </div>

        {/* Update Button */}
        <button className="update-button">Update</button>
      </div>

      {/* Log Out */}
      <div className="logout">Log out</div>
    </div>
  );
}

export default Settings;
