import React from 'react';

import { useState } from'react';
import "./Workspace.css";
import textIcon from "./icons/text.png"; // Replace with actual image paths
import imageIcon from "./icons/image.png";
import videoIcon from "./icons/video.png";
import gifIcon from "./icons/gif.png";
import emailIcon from "./icons/email.png";
import phoneIcon from "./icons/phone.png";
import dateIcon from "./icons/date.png";
import ratingIcon from "./icons/rating.png";
import buttonIcon from "./icons/buttons.png";
import flagIcon from "./icons/flag.png";

const Workspace = () => {

 const [isLightTheme, setIsLightTheme] = useState(false);  

  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  return (
    <nav className="workspace-nav">
      <div className="rename">
        <input type="text" className="rename-input" placeholder="Enter Form Name"></input>
      </div>
      <div className="mid-button">
        
        <div>
        <button className="flow-button">Flow</button>
        
        </div>
        <div>
        
        <button className="Response-button">Response</button>
        </div>
      </div>

     <div className='rest-buttons'>
     <div className="theme-toggle">
              <span>Light</span>
              <label className="switch">
                <input
                  type="checkbox"
                  checked={!isLightTheme}
                  onChange={toggleTheme}
                />
                <span className="slider"></span>
              </label>
              <span>Dark</span>
            </div>

            <button className="share-from-button">Share</button>
            <button className="save-button">Save</button>
            <button className="close-button"><img src='close.png'></img></button>
            
            </div>

    </nav>

    



  );
};

export default Workspace;
