import React, { useState } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings";
import ShareModal from "./ShareModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [folders, setFolders] = useState([]);
  const [forms, setForms] = useState({});
  const [activeFolder, setActiveFolder] = useState(null);
  const [deleteFolderIndex, setDeleteFolderIndex] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  const handleCreateFolder = (folderName) => {
    if (folderName.trim()) {
      setFolders([...folders, folderName.trim()]);
      setForms({ ...forms, [folderName.trim()]: [] });
    }
    closeModal();
  };

  const handleCreateForm = (formName) => {
    if (activeFolder && formName.trim()) {
      const updatedForms = { ...forms };
      updatedForms[activeFolder].push(formName.trim());
      setForms(updatedForms);
    }
    closeModal();
  };

  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen((prevState) => !prevState);
  };

  const handleDeleteFolder = (index) => {
    const updatedFolders = folders.filter((_, i) => i !== index);
    const folderToDelete = folders[index];
    const updatedForms = { ...forms };
    delete updatedForms[folderToDelete];
    setFolders(updatedFolders);
    setForms(updatedForms);
    if (activeFolder === folderToDelete) setActiveFolder(null);
  };

  const openDeleteFolderModal = (index) => {
    setDeleteFolderIndex(index);
    setIsDeleteModalOpen(true);
  };

  const closeDeleteFolderModal = () => {
    setDeleteFolderIndex(null);
    setIsDeleteModalOpen(false);
  };

  const confirmDeleteFolder = () => {
    if (deleteFolderIndex !== null) {
      handleDeleteFolder(deleteFolderIndex);
    }
    closeDeleteFolderModal();
  };

  const setActiveFolderHandler = (folderName) => {
    setActiveFolder(folderName);
  };

  return (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      <nav className="navbar">
        <div className="workspace-container">
          <button className="workspace-button" onClick={toggleDropdown}>
            <span>Dewank Rastogi's workspace</span>
          </button>
          {isDropdownOpen && (
            <div className="dropdown-menu">
              <button className="dropdown-item">Settings</button>
              <button className="dropdown-item logout">Log Out</button>
            </div>
          )}
        </div>

        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={!isLightTheme}
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
      </nav>

      <div className="Dashboard-content">
        <div className="folder-section">
          <button className="create-folder" onClick={() => openModal("folder")}>
            Create Folder
          </button>
          <div className="folder-list">
            {folders.map((folder, index) => (
              <div
                key={index}
                className={`folder-item ${folder === activeFolder ? "active" : ""}`}
                onClick={() => setActiveFolderHandler(folder)}
              >
                <span>{folder}</span>
                <button
                  className="delete-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    openDeleteFolderModal(index);
                  }}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </div>

        <div className="form-section">
          {activeFolder ? (
            <>
              <h2>{`Forms in ${activeFolder}`}</h2>
              <button
                className="create-form"
                onClick={() => openModal("form")}
              >
                Create Form
              </button>
              <ul className="form-list">
                {forms[activeFolder]?.map((form, index) => (
                  <li key={index} className="form-item">
                    {form}
                  </li>
                ))}
              </ul>
            </>
          ) : (
            <p>Select a folder to view its forms.</p>
          )}
        </div>
      </div>

      {isModalOpen && modalType === "folder" && (
        <CreateFolderModal closeModal={closeModal} onCreateFolder={handleCreateFolder} />
      )}
      {isModalOpen && modalType === "form" && (
        <CreateFormModal closeModal={closeModal} onCreateForm={handleCreateForm} />
      )}
      {isDeleteModalOpen && (
        <DeleteFolder
          closeModal={closeDeleteFolderModal}
          onConfirm={confirmDeleteFolder}
        />
      )}
    </div>
  );
};

export default Dashboard;
