import React, { useState } from "react";
import Response from "./Response"; // Import the Response component
import "./Workspacenavbar.css";

const Workspacenavbar = () => {
  const [showResponse, setShowResponse] = useState(false);

  const handleResponseClick = () => {
    setShowResponse(true);
  };

  if (showResponse) {
    return <Response />; // Show the Response component when the state is true
  }

  return (
    <nav className="workspace-nav">
      {/* Form Name Input */}
      <div className="rename">
        <input
          type="text"
          className="rename-input"
          placeholder="Enter Form Name"
        />
      </div>

      {/* Mid Section Buttons */}
      <div className="mid-button">
        <button className="flow-button active">Flow</button>
        <button className="response-button" onClick={handleResponseClick}>
          Response
        </button>
      </div>

      {/* Right Side Buttons */}
      <div className="rest-buttons">
        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={false} // Assuming initial value
              onChange={() => {}}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-from-button">Share</button>
        <button className="save-button">Save</button>
        <button className="close-button"><img src="close.png" alt="Close" /></button>
      </div>
    </nav>
  );
};

export default Workspacenavbar;
