import React, { useState } from 'react';
import './App.css'; // Default styling
import MainPage from './Components/Main-page/Mainpage';
import LoginPage from './Components/Login-page/LoginPage';
import Sign from './Components/Sign/Sign'; // Make sure to import the Sign component

function App() {
  const [currentPage, setCurrentPage] = useState('main'); // State to manage the current page

  const handleNavigateToLogin = () => {
    setCurrentPage('login');
  };

  const handleNavigateToSignUp = () => {
    setCurrentPage('signup'); // Navigate to the signup page
  };

  const handleNavigateToMain = () => {
    setCurrentPage('main');
  };

  return (
    <div className="App">
      {currentPage === 'main' && <MainPage onNavigateToLogin={handleNavigateToLogin} />}
      {currentPage === 'login' && <LoginPage onBackClick={handleNavigateToMain} onNavigateToSignUp={handleNavigateToSignUp} />}
      {currentPage === 'signup' && <Sign />}
    </div>
  );
}

export default App;
