import React, { useState, useEffect } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings";
import ShareModal from "./ShareModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [folders, setFolders] = useState([]);
  const [forms, setForms] = useState([]);
  const [deleteFolderIndex, setDeleteFolderIndex] = useState(null);
  const [deleteFormIndex, setDeleteFormIndex] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isDeleteFormModalOpen, setIsDeleteFormModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);
  const [dashboardHeight, setDashboardHeight] = useState("100vh");

  useEffect(() => {
    // Dynamically calculate height based on content
    const newHeight = 100 + (folders.length + forms.length) * 10; // Example logic
    setDashboardHeight(`${Math.max(newHeight, 100)}vh`);
  }, [folders, forms]);

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  const handleCreateFolder = (folderName) => {
    if (folderName.trim()) {
      setFolders([...folders, folderName.trim()]);
    }
    closeModal();
  };

  const handleCreateForm = (formName) => {
    if (formName.trim()) {
      setForms([...forms, formName.trim()]);
    }
    closeModal();
  };

  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen((prevState) => !prevState);
  };

  const handleDeleteFolder = (index) => {
    const updatedFolders = folders.filter((_, i) => i !== index);
    setFolders(updatedFolders);
  };

  const handleDeleteForm = (index) => {
    const updatedForms = forms.filter((_, i) => i !== index);
    setForms(updatedForms);
  };

  return (
    <div
      className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}
      style={{ height: dashboardHeight }}
    >
      {isSettingsOpen ? (
        <Settings closeSettings={() => setIsSettingsOpen(false)} />
      ) : (
        <>
          <nav className="navbar">
            <div className="workspace-container">
              <button className="workspace-button" onClick={toggleDropdown}>
                <span>Dewank Rastogi's workspace</span>
                <img className="dropdown-icon" src="drop.png" alt="Dropdown Icon" />
              </button>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <button className="dropdown-item" onClick={() => setIsSettingsOpen(true)}>
                    Settings
                  </button>
                  <button className="dropdown-item logout">Log Out</button>
                </div>
              )}
            </div>
            <div className="theme-toggle">
              <span>Light</span>
              <label className="switch">
                <input
                  type="checkbox"
                  checked={!isLightTheme}
                  onChange={toggleTheme}
                />
                <span className="slider"></span>
              </label>
              <span>Dark</span>
            </div>
            <button className="share-button" onClick={() => setIsShareModalOpen(true)}>
              Share
            </button>
          </nav>

          <div className="Dashboard-content">
            <ul className="Create-folder-list">
              <li>
                <button className="create-folder" onClick={() => openModal("folder")}>
                  <img src="folder.png" className="folder" alt="Folder" />
                  Create a folder
                </button>
              </li>
              <li>
                <div className="folder-list">
                  {folders.map((folder, index) => (
                    <div key={index} className="folder-item">
                      <span>{folder}</span>
                      <button
                        className="delete-button"
                        onClick={() => handleDeleteFolder(index)}
                      >
                        <img src="del.png" alt="Delete" />
                      </button>
                    </div>
                  ))}
                </div>
              </li>
            </ul>
            <ul className="Create-form-list">
              <li>
                <button className="create-typebot" onClick={() => openModal("form")}>
                  <p>
                    <span className="plus">+</span> <br />
                    Create a typebot
                  </p>
                </button>
              </li>
              <li>
                <div className="form-list">
                  {forms.map((form, index) => (
                    <div key={index} className="form-item">
                      <span>{form}</span>
                      <button
                        className="delete-button-form"
                        onClick={() => handleDeleteForm(index)}
                      >
                        <img src="del.png" className="del-form" alt="Delete" />
                      </button>
                    </div>
                  ))}
                </div>
              </li>
            </ul>
          </div>
          {isModalOpen && modalType === "folder" && (
            <CreateFolderModal
              closeModal={closeModal}
              isLightTheme={isLightTheme}
              onCreateFolder={handleCreateFolder}
            />
          )}
          {isModalOpen && modalType === "form" && (
            <CreateFormModal
              closeModal={closeModal}
              isLightTheme={isLightTheme}
              onCreateForm={handleCreateForm}
            />
          )}
          {isShareModalOpen && <ShareModal isOpen onClose={() => setIsShareModalOpen(false)} />}
        </>
      )}
    </div>
  );
};

export default Dashboard;
