import React, { useState } from "react";
import "./Workspacenavbar.css";

const ResponseNavbar = () => {
  const [isLightTheme, setIsLightTheme] = useState(false);

  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  return (
    <nav className="workspace-nav">
      {/* Form Name Input */}
      <div className="rename">
        <input
          type="text"
          className="rename-input"
          placeholder="Enter Form Name"
        />
      </div>

      {/* Mid Section Buttons */}
      <div className="mid-button">
        <button className="flow-button active">Flow</button>
        <button className="response-button">Response</button>
      </div>

      {/* Right Side Buttons */}
      <div className="rest-buttons">
        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={isLightTheme}
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-from-button">Share</button>
        <button className="save-button">Save</button>
        <button className="close-button"><img src="close.png"></img></button>
      </div>
    </nav>
  );
};

export default  ResponseNavbar;
