import React from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import MainPage from './Components/Main-page/Mainpage';
import LoginPage from './Components/Login-page/LoginPage';
import Sign from './Components/Sign-page/Sign'; // Signup page
import Dashboard from './Components/Dashboard/Dashboard';
import Workspace from './Components/Workspace/Workspace';
import Response from './Components/Workspace/Response'; // Import additional components if required
import Settings from './Components/Dashboard/Settings';
import ShareModal from './Components/Dashboard/ShareModal';

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Main Page */}
          <Route path="/" element={<MainPage />} />

          {/* Login Page */}
          <Route path="/login" element={<LoginPage />} />

          {/* Signup Page */}
          <Route path="/signup" element={<Sign />} />

          {/* Private Route for Dashboard */}
          <Route path="/dashboard" element={<PrivateRoute />}>
            <Route path="/dashboard" element={<Dashboard />} />
          </Route>

          {/* Workspace */}
          <Route path="/workspace" element={<Workspace />} />

          {/* Response Component */}
          <Route path="/response" element={<Response />} />

          {/* Settings */}
          <Route path="/settings" element={<Settings />} />

          {/* Share Modal */}
          <Route path="/share" element={<ShareModal />} />
        </Routes>
      </div>
    </Router>
  );
}

const PrivateRoute = () => {
  if (!localStorage.getItem('jwtToken')) {
    // If no token found, redirect to the login page
    return <Navigate to="/login" />;
  }
  return <Dashboard />;
};

export default App;
