import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";  // to redirect user if token is not found
import "./Settings.css";

const Settings = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showOldPassword, setShowOldPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const history = useHistory();  // used for redirection if no token

  useEffect(() => {
    // Check if the token is available in localStorage when the component is mounted
    const token = localStorage.getItem("jwtToken");
    if (!token) {
      alert("No token found. Please log in.");
      history.push("/login");  // Redirect to login page
    }
  }, [history]);

  const handleUpdate = async () => {
    const token = localStorage.getItem("jwtToken");

    if (!token) {
      alert("No token found. Please log in.");
      history.push("/login");
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/update", {
        method: "PUT",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, email, oldPassword, newPassword }), // userId is not needed
      });

      const data = await response.json();

      if (response.ok) {
        alert("Update successful");
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error updating user:", error);
      alert("Something went wrong. Please try again.");
    }
  };

  return (
    <div className="container">
      <h1 className="title">Settings</h1>
      <div className="form">
        <div className="inputContainer">
          <img src="user.png" alt="User Icon" className="icon" />
          <input
            type="text"
            placeholder="Name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input"
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Email Icon" className="icon" />
          <input
            type="email"
            placeholder="Update Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input"
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showOldPassword ? "text" : "password"}
            placeholder="Old Password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowOldPassword(!showOldPassword)}
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showNewPassword ? "text" : "password"}
            placeholder="New Password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowNewPassword(!showNewPassword)}
          />
        </div>

        <button className="update-button" onClick={handleUpdate}>
          Update
        </button>
      </div>

      <div className="logoutContainer">
        <img src="logout.png" alt="Logout Icon" className="logoutIcon" />
        <button
          className="logoutButton"
          onClick={() => {
            // Remove user data and JWT token from localStorage
            localStorage.removeItem("jwtToken");
            history.push("/login"); // Redirect to login page
          }}
        >
          Log out
        </button>
      </div>
    </div>
  );
};

export default Settings;
