import React, { useState, useEffect } from "react";
import "./Dashboard.css";
import axios from "axios";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings";
import ShareModal from "./ShareModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [folders, setFolders] = useState([]);
  const [forms, setForms] = useState([]);
  const [username, setUsername] = useState("");
  const [deleteFolderIndex, setDeleteFolderIndex] = useState(null);
  const [deleteFormIndex, setDeleteFormIndex] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isDeleteFormModalOpen, setIsDeleteFormModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);
  const [activeFolderIndex, setActiveFolderIndex] = useState(null);

  useEffect(() => {
    const fetchUsername = async () => {
      try {
        const token = localStorage.getItem("jwtToken");
        if (token) {
          const response = await axios.get("http://localhost:8000/username", {
            headers: { Authorization: `Bearer ${token}` },
          });
          if (response.data?.username) {
            setUsername(response.data.username);
          }
        }
      } catch (error) {
        console.error("Error fetching username:", error);
      }
    };
    fetchUsername();
  }, []);

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  const handleCreateFolder = async (folderName) => {
    if (folderName.trim()) {
      try {
        const response = await axios.post('http://localhost:8000/api/folders', { name: folderName.trim() });
        setFolders([...folders, response.data]);
      } catch (error) {
        if (error.response?.status === 409) {
          alert('Folder name must be unique.');
        } else {
          console.error('Error creating folder:', error);
          alert('Failed to create folder. Please try again.');
        }
      }
    }
    closeModal();
  };
  
  useEffect(() => {
    const fetchFolders = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/folders');
        setFolders(response.data);
      } catch (error) {
        console.error('Error fetching folders:', error);
      }
    };
    fetchFolders();
  }, []);
  
useEffect(() => {
  const fetchForms = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/forms');
      setForms(response.data);
    } catch (error) {
      console.error('Error fetching forms:', error);
    }
  };

  fetchForms();
}, []);


const handleCreateForm = async (formName) => {
  if (formName.trim() && activeFolderIndex !== null) {
    try {
      const folderId = folders[activeFolderIndex]._id;
      const response = await axios.post('http://localhost:8000/api/forms', {
        name: formName.trim(),
        folderId,
      });
      const updatedFolders = [...folders];
      updatedFolders[activeFolderIndex].forms.push(response.data);
      setFolders(updatedFolders);
    } catch (error) {
      console.error('Error creating form:', error);
      alert('Failed to create form. Please try again.');
    }
  }
  closeModal();
};


  const toggleTheme = () => {
    setIsLightTheme((prev) => !prev);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen((prev) => !prev);
  };

  const handleDeleteFolder = (index) => {
    setFolders(folders.filter((_, i) => i !== index));
  };

  const handleDeleteForm = (index) => {
    setForms(forms.filter((_, i) => i !== index));
  };

  const openDeleteFolderModal = (index) => {
    setDeleteFolderIndex(index);
    setIsDeleteModalOpen(true);
  };

  const openDeleteFormModal = (index) => {
    setDeleteFormIndex(index);
    setIsDeleteFormModalOpen(true);
  };

  const closeDeleteFolderModal = () => {
    setDeleteFolderIndex(null);
    setIsDeleteModalOpen(false);
  };

  const closeDeleteFormModal = () => {
    setDeleteFormIndex(null);
    setIsDeleteFormModalOpen(false);
  };

  const confirmDeleteFolder = () => {
    if (deleteFolderIndex !== null) handleDeleteFolder(deleteFolderIndex);
    closeDeleteFolderModal();
  };

  const confirmDeleteForm = () => {
    if (deleteFormIndex !== null) handleDeleteForm(deleteFormIndex);
    closeDeleteFormModal();
  };

  const openSettings = () => setIsSettingsOpen(true);

  const closeSettings = () => setIsSettingsOpen(false);

  const openShareModal = () => setIsShareModalOpen(true);

  const closeShareModal = () => setIsShareModalOpen(false);

  const toggleFolderVisibility = (index) => {
    setActiveFolderIndex(activeFolderIndex === index ? null : index);
  };

  return (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      {isSettingsOpen ? (
        <Settings closeSettings={closeSettings} />
      ) : (
        <>
          <nav className="navbar">
            <div className="logo"></div>
            <div className="workspace-container">
              <button className="workspace-button" onClick={toggleDropdown}>
                <span>{username ? `${username}'s workspace` : "Loading..."}</span>
                <img className="dropdown-icon" src="drop.png" alt="Dropdown Icon" />
              </button>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <button className="dropdown-item" onClick={openSettings}>
                    Settings
                  </button>
                  <button className="dropdown-item logout">Log Out</button>
                </div>
              )}
            </div>
            <div className="theme-toggle">
              <span>Light</span>
              <label className="switch">
                <input type="checkbox" checked={!isLightTheme} onChange={toggleTheme} />
                <span className="slider"></span>
              </label>
              <span>Dark</span>
            </div>
            <button className="share-button" onClick={openShareModal}>
              Share
            </button>
          </nav>

          <div className="Dashboard-content">
            <ul className="Create-folder-list">
              <li>
                <button
                  className={`create-folder ${activeFolderIndex !== null ? "hidden" : ""}`}
                  onClick={() => openModal("folder")}
                >
                  <img src="folder.png" className="folder" alt="Folder" />
                  Create a folder
                </button>
              </li>
              <li>
                <button
                  className={`create-typebot ${activeFolderIndex !== null ? "" : "hidden"}`}
                  onClick={() => openModal("form")}
                >
                  <p>
                    <span className="plus">+</span> <br />
                    Create a typebot
                  </p>
                </button>
              </li>
            </ul>

            <ul className="Create-form-list">
              <li className="folder-list-li">
                <div className="folder-list">
                  {folders.map((folder, index) => (
                    <div
                      key={index}
                      className={`folder-item ${activeFolderIndex === index ? "active-folder" : ""}`}
                    >
                      <span onClick={() => toggleFolderVisibility(index)}>
                        {folder.name}
                      </span>
                      <button
                        className="delete-button"
                        onClick={() => openDeleteFolderModal(index)}
                      >
                        <img src="del.png" alt="Delete" />
                      </button>
                    </div>
                  ))}
                </div>
                {activeFolderIndex !== null && (
                  <ul className="form-list">
                    {folders[activeFolderIndex]?.forms.map((form, idx) => (
                      <div key={idx} className="form-item">
                        <span>{form}</span>
                        <button
                          className="delete-button-form"
                          onClick={() => openDeleteFormModal(idx)}
                        >
                          <img src="del.png" className="del-form" alt="Delete" />
                        </button>
                      </div>
                    ))}
                  </ul>
                )}
              </li>
              <li className="form-li">
                <div className="form-list">
                  {forms.map((form, index) => (
                    <div key={index} className="form-item">
                      <span>{form}</span>
                      <button
                        className="delete-button-form"
                        onClick={() => openDeleteFormModal(index)}
                      >
                        <img src="del.png" className="del-form" alt="Delete" />
                      </button>
                    </div>
                  ))}
                </div>
              </li>
            </ul>
          </div>

          {isModalOpen && modalType === "folder" && (
            <CreateFolderModal
              closeModal={closeModal}
              isLightTheme={isLightTheme}
              onCreateFolder={handleCreateFolder}
            />
          )}
          {isModalOpen && modalType === "form" && (
            <CreateFormModal
              closeModal={closeModal}
              isLightTheme={isLightTheme}
              onCreateForm={handleCreateForm}
            />
          )}

          {isDeleteModalOpen && (
            <DeleteFolder
              closeModal={closeDeleteFolderModal}
              isLightTheme={isLightTheme}
              confirmDeleteFolder={confirmDeleteFolder}
            />
          )}

          {isDeleteFormModalOpen && (
            <DeleteForm
              closeModal={closeDeleteFormModal}
              isLightTheme={isLightTheme}
              confirmDeleteForm={confirmDeleteForm}
            />
          )}

          {isShareModalOpen && <ShareModal closeShareModal={closeShareModal} />}
        </>
      )}
    </div>
  );
};

export default Dashboard;
