import React, { useState, useEffect } from "react";
import "./Settings.css";

const Settings = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [showOldPassword, setShowOldPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);

  // Fetch current username and email from server
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const token = localStorage.getItem('token'); // Assuming the token is stored in localStorage
        const response = await fetch("http://localhost:8000/username", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`, // Include the JWT in the Authorization header
          },
        });
        // Handle the response as before
      } catch (error) {
        console.error("Error fetching user details:", error);
        alert("Something went wrong. Please try again later.");
      }
    };
    
    const handleUpdate = async () => {
      if (!username || !email || !oldPassword || !newPassword) {
        alert("All fields are required.");
        return;
      }
    
      try {
        const token = localStorage.getItem('token'); // Get the JWT token from localStorage
        const response = await fetch("http://localhost:8000/update", {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`, // Include the JWT in the Authorization header
          },
          body: JSON.stringify({ username, email, oldPassword, newPassword }),
        });
    
        const data = await response.json();
        if (response.ok) {
          alert("Update successful");
          // Clear fields and handle success as before
        } else {
          alert(data.message); // Display specific error message from server
        }
      } catch (error) {
        console.error("Error updating user:", error);
        alert("Something went wrong. Please try again.");
      }
    };
    
    
    fetchUserDetails();
  }, []);




  
  // Handle update request
  const handleUpdate = async () => {
    if (!username || !email || !oldPassword || !newPassword) {
      alert("All fields are required.");
      return;
    }
  
    try {
      const response = await fetch("http://localhost:8000/username", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include", 
        body: JSON.stringify({ username, email, oldPassword, newPassword }),
      });
  
      const data = await response.json();
      if (response.ok) {
        alert("Update successful");
        setUsername(""); // Clear fields
        setEmail("");
        setOldPassword("");
        setNewPassword("");
      } else {
        alert(data.message); // Display specific error message from server
      }
    } catch (error) {
      console.error("Error updating user:", error);
      alert("Something went wrong. Please try again.");
    }
  };
  

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch("http://localhost:8000/logout", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`, // Pass the token
        },
      });
      if (response.ok) {
        alert("Logged out successfully");
        localStorage.removeItem('token'); // Clear token
        window.location.href = "/login";
      } else {
        alert("Error logging out");
      }
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };
  

  return (
    <div className="container">
      <h1 className="title">Settings</h1>
      <div className="form">
        <div className="inputContainer">
          <img src="user.png" alt="User Icon" className="icon" />
          <input
            type="text"
            placeholder="Name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="input"
          />
        </div>

        <div className="inputContainer">
          <img src="email-icon.png" alt="Email Icon" className="icon" />
          <input
            type="email"
            placeholder="Update Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input"
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showOldPassword ? "text" : "password"}
            placeholder="Old Password"
            value={oldPassword}
            onChange={(e) => setOldPassword(e.target.value)}
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowOldPassword(!showOldPassword)}
          />
        </div>

        <div className="inputContainer">
          <img src="lock.png" alt="Lock Icon" className="icon" />
          <input
            type={showNewPassword ? "text" : "password"}
            placeholder="New Password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            className="input"
          />
          <img
            src="eye.png"
            alt="Toggle Visibility"
            className="eyeIcon"
            onClick={() => setShowNewPassword(!showNewPassword)}
          />
        </div>

        <button onClick={handleUpdate} className="updateButton">
          Update
        </button>

        <button onClick={handleLogout} className="logoutButton">
          Logout
        </button>
      </div>
    </div>
  );
};

export default Settings;
