import React, { useState } from "react";
import "./FlowDisplay.css";

const FlowDisplay = ({ workflowItems }) => {
  const [responses, setResponses] = useState({});

  const handleInputChange = (id, value) => {
    setResponses((prev) => ({
      ...prev,
      [id]: value,
    }));
  };

  return (
    <div className="flow-display-container">
      <h2>Interactive Flow</h2>
      <div className="flow-items">
        {workflowItems.length === 0 ? (
          <p>No components added to the flow yet.</p>
        ) : (
          workflowItems.map((item, index) => (
            <div className="flow-item" key={index}>
              {React.cloneElement(item, {
                onChange: (value) => handleInputChange(index, value),
                value: responses[index] || "",
              })}
            </div>
          ))
        )}
      </div>
      <div className="flow-responses">
        <h3>Responses</h3>
        <ul>
          {Object.entries(responses).map(([key, value]) => (
            <li key={key}>
              <strong>Component {Number(key) + 1}:</strong> {value || "No response"}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default FlowDisplay;
