import React, { useState } from "react";
import "./CreateFolderModal.css";

const CreateFolderModal = ({ closeModal, isLightTheme, onCreateFolder }) => {
  const [folderName, setFolderName] = useState("");

  const handleCreate = async () => {
    try {
      // Get the JWT token from localStorage
      const token = localStorage.getItem('token'); // Ensure the token is saved after login
      if (!token) {
        alert("User is not logged in");
        return;
      }

      // Make the API request to create the folder
      const response = await fetch("http://localhost:5000/create-folder", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`, // Add token to Authorization header
        },
        body: JSON.stringify({
          folderName,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        onCreateFolder(data.folder); // Pass folder data to parent component
      } else {
        alert(data.message);  // Show error message
      }
    } catch (error) {
      console.error(error);
      alert("An error occurred while creating the folder");
    }
  };

  return (
    <div className={`modal-overlay ${isLightTheme ? "light" : "dark"}`}>
      <div className={`modal-container ${isLightTheme ? "light" : "dark"}`}>
        <h3 className="modal-title">Create New Folder</h3>
        <input
          type="text"
          placeholder="Enter folder name"
          className="modal-input"
          value={folderName}
          onChange={(e) => setFolderName(e.target.value)}
        />
        <div className="modal-buttons">
          <button className="modal-button done" onClick={handleCreate}>
            Done
          </button>
          <h3 className="bar">|</h3>
          <button className="modal-button cancel" onClick={closeModal}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateFolderModal;
