import React, { useState } from "react";
import "./InputComp.css";

const InputComp = () => {
  const [inputValue, setInputValue] = useState("");
  const [isFocused, setIsFocused] = useState(false);

  return (
    <div className="container">
      <label htmlFor="input-box" className="label">
        Text
      </label>
      <div
        className={`input-wrapper ${!inputValue && !isFocused ? "error" : ""}`}
      >
        <div className="icon">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24"
            width="24"
            fill="#0066ff"
          >
            <path d="M12 20.95q-3.15 0-5.462-2.312Q4.225 16.325 4.225 13.175q0-3.15 2.313-5.462Q8.85 5.4 12 5.4q3.15 0 5.462 2.313 2.313 2.313 2.313 5.462 0 3.15-2.313 5.462Q15.15 20.95 12 20.95Zm-1.575-8.6q-.675 0-1.163-.487-.487-.487-.487-1.162 0-.675.487-1.162.488-.488 1.163-.488.675 0 1.162.488.488.487.488 1.162 0 .675-.488 1.162-.487.487-1.162.487Zm2.975 0q-.675 0-1.163-.487-.487-.487-.487-1.162 0-.675.487-1.162.488-.488 1.163-.488.675 0 1.162.488.488.487.488 1.162 0 .675-.488 1.162-.487.487-1.162.487Z" />
          </svg>
        </div>
        <input
          id="input-box"
          type="text"
          className="input"
          placeholder="Click here to edit"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
        />
      </div>
      {!inputValue && !isFocused && (
        <span className="error-message">Required Field</span>
      )}
      <button className="delete-btn">
        <img src="delete.png"></img>
      </button>
    </div>
  );
};

export default InputComp;
