import React, { useState } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings";
import ShareModal from "./ShareModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  const toggleTheme = () => {
    setIsLightTheme((prevState) => !prevState);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen((prevState) => !prevState);
  };

  const openSettings = () => {
    setIsSettingsOpen(true);
  };

  const closeSettings = () => {
    setIsSettingsOpen(false);
  };

  const openShareModal = () => {
    setIsShareModalOpen(true);
  };

  const closeShareModal = () => {
    setIsShareModalOpen(false);
  };

  return (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      <nav className="navbar">
        <div className="workspace-container">
          <button className="workspace-button" onClick={toggleDropdown}>
            <span>Dewank Rastogi's workspace</span>
            <img className="dropdown-icon" src="drop.png" alt="Dropdown Icon" />
          </button>
          {isDropdownOpen && (
            <div className="dropdown-menu">
              <button className="dropdown-item" onClick={openSettings}>
                Settings
              </button>
              <button className="dropdown-item logout">Log Out</button>
            </div>
          )}
        </div>

        <div className="theme-toggle">
          <span>Light</span>
          <label className="switch">
            <input
              type="checkbox"
              checked={!isLightTheme}
              onChange={toggleTheme}
            />
            <span className="slider"></span>
          </label>
          <span>Dark</span>
        </div>
        <button className="share-button" onClick={openShareModal}>
          Share
        </button>
      </nav>

      <div className="Dashboard-content">
        {/* Folder and Form Content Here */}
      </div>

      {isSettingsOpen && <Settings closeSettings={closeSettings} />}

      {isShareModalOpen && <ShareModal isOpen={isShareModalOpen} onClose={closeShareModal} />}

      {/* Existing Modals */}
      {isModalOpen && modalType === "folder" && (
        <CreateFolderModal
          closeModal={closeModal}
          isLightTheme={isLightTheme}
          onCreateFolder={(folderName) => console.log(folderName)}
        />
      )}
      {isModalOpen && modalType === "form" && (
        <CreateFormModal
          closeModal={closeModal}
          isLightTheme={isLightTheme}
          onCreateForm={(formName) => console.log(formName)}
        />
      )}
    </div>
  );
};

export default Dashboard;
