import React, { useState } from 'react';
import './App.css';
import Main from './Components/Main-page/Main';
import Sign from './Components/Sign/Sign';
import Navbar from './Components/Navbar/Navbar';

function App() {
  const [currentPage, setCurrentPage] = useState('main'); // State to manage the current page

  const handleNavigateToLogin = () => {
    setCurrentPage('login'); // Navigate to login page
  };

  const handleNavigateToSignUp = () => {
    setCurrentPage('signup'); // Navigate to signup page
  };

  const handleNavigateToMain = () => {
    setCurrentPage('main'); // Navigate to main page
  };

  return (
    <div className="App">
      {/* Render the Navbar */}
      <Navbar onNavigateToLogin={handleNavigateToLogin} />

      {/* Render pages based on currentPage */}
      {currentPage === 'main' && <Main onNavigateToLogin={handleNavigateToLogin} />}
      {currentPage === 'signup' && <Sign onBackClick={handleNavigateToMain} onNavigateToLogin={handleNavigateToLogin} />}
      {currentPage === 'login' && (
        <div>
          <h2>Login Page</h2>
          {/* Replace with your Login component */}
          <button onClick={handleNavigateToMain}>Back to Main</button>
        </div>
      )}
    </div>
  );
}

export default App;
