import React, { useState } from 'react';
import './App.css';
import MainPage from './Components/Main-page/Mainpage';
import LoginPage from './Components/Login-page/LoginPage';
import Sign from './Components/Sign-page/Sign'; // Import Sign (Signup) page
import Dashboard from './Components/Dashboard/Dashboard'; // Import Dashboard component
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Routes replaces Switch in v6



function App() {
  const [currentPage, setCurrentPage] = useState('main'); // State to manage the current page

  const handleNavigateToLogin = () => {
    setCurrentPage('login'); // Navigate to login page
  };

  const handleNavigateToSignUp = () => {
    setCurrentPage('signup'); // Navigate to signup page
  };

  const handleNavigateToDashboard = () => {
    setCurrentPage('dashboard'); // Navigate to dashboard after login
  };

  const handleNavigateToMain = () => {
    setCurrentPage('main'); // Navigate to main page
  };

  return (
    <div className="App">
        <Router>
        <Routes>
          {/* Route for the MainPage */}
          <Route path="/" element={<MainPage />} />

          {/* Route for the LoginPage */}
          <Route path="/login" element={<LoginPage />} />

          {/* Route for the SignUp page */}
          <Route path="/signup" element={<Sign />} />

          {/* Route for the Dashboard */}
          <Route path="/dashboard" element={<Dashboard />} />
        </Routes>
      </Router>
      {currentPage === 'main' && <MainPage onNavigateToLogin={handleNavigateToLogin} />}
      {currentPage === 'login' && (
        <LoginPage
          onBackClick={handleNavigateToMain}
          onNavigateToSignUp={handleNavigateToSignUp} // Added handler to navigate to signup
          onLoginSuccess={handleNavigateToDashboard}
        />
      )}
      {currentPage === 'signup' && (
        <Sign
          onBackClick={handleNavigateToLogin} // Navigate back to login from signup
          onNavigateToLogin={handleNavigateToLogin} // Navigate to login from signup
        />
      )}
      {currentPage === 'dashboard' && <Dashboard />}

    </div>
  );
}

export default App;
