import React, { useState, useEffect } from "react";
import "./Dashboard.css";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings";
import ShareModal from "./ShareModal";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [folders, setFolders] = useState([]); 
  const [forms, setForms] = useState([]); 
  const [deleteFolderIndex, setDeleteFolderIndex] = useState(null);
  const [deleteFormIndex, setDeleteFormIndex] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isDeleteFormModalOpen, setIsDeleteFormModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);
  const [activeFolderIndex, setActiveFolderIndex] = useState(null);

  useEffect(() => {
    // Fetch the folders and forms when the component mounts
    fetchFolders();
    fetchForms();
  }, []);

  const fetchFolders = async () => {
    try {
      const response = await fetch("/folders", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        }
      });
      const data = await response.json();
      setFolders(data);
    } catch (err) {
      console.error("Error fetching folders:", err);
    }
  };

  const fetchForms = async () => {
    try {
      const response = await fetch("/forms", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("token")}`
        }
      });
      const data = await response.json();
      setForms(data);
    } catch (err) {
      console.error("Error fetching forms:", err);
    }
  };

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  const handleCreateFolder = async (folderName) => {
    if (folderName.trim()) {
      const isDuplicate = folders.some(folder => folder.name === folderName.trim());
      if (isDuplicate) {
        alert("Folder name must be unique.");
        return;
      }

      try {
        const response = await fetch("/folders", {
          method: "POST",
          headers: {
            "Authorization": `Bearer ${localStorage.getItem("token")}`,
            "Content-Type": "application/json"
          },
          body: JSON.stringify({ name: folderName.trim() })
        });
        const newFolder = await response.json();
        setFolders([...folders, newFolder]);
      } catch (err) {
        console.error("Error creating folder:", err);
      }
    }
    closeModal();
  };

  const handleCreateForm = async (formName, folderIndex = null) => {
    if (formName.trim()) {
      const formData = { name: formName.trim(), folderId: folderIndex ? folders[folderIndex]._id : null };

      try {
        const response = await fetch("/forms", {
          method: "POST",
          headers: {
            "Authorization": `Bearer ${localStorage.getItem("token")}`,
            "Content-Type": "application/json"
          },
          body: JSON.stringify(formData)
        });
        const newForm = await response.json();

        if (folderIndex !== null) {
          const updatedFolders = [...folders];
          updatedFolders[folderIndex].forms.push(newForm._id);
          setFolders(updatedFolders);
        } else {
          setForms([...forms, newForm]);
        }
      } catch (err) {
        console.error("Error creating form:", err);
      }
    }
    closeModal();
  };

  return (
    <div className={`dashboard-container ${isLightTheme ? "light" : "dark"}`}>
      <div className="Dashboard-content">
        <ul className="Create-folder-list">
          <li>
            <button className="create-folder" onClick={() => openModal("folder")}>
              Create a folder
            </button>
          </li>
          <li>
            <button className="create-typebot" onClick={() => openModal("form")}>
              Create a typebot
            </button>
          </li>
        </ul>

        <ul className="Create-form-list">
          {folders.map((folder, index) => (
            <div key={index} className="folder-item">
              <span onClick={() => toggleFolderVisibility(index)}>{folder.name}</span>
              {activeFolderIndex === index && (
                <ul className="form-list">
                  {folder.forms.map((form, idx) => (
                    <div key={idx} className="form-item">
                      <span>{form.name}</span>
                    </div>
                  ))}
                </ul>
              )}
            </div>
          ))}
        </ul>

        {isModalOpen && modalType === "folder" && (
          <CreateFolderModal closeModal={closeModal} onCreateFolder={handleCreateFolder} />
        )}
        {isModalOpen && modalType === "form" && (
          <CreateFormModal closeModal={closeModal} onCreateForm={handleCreateForm} />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
