import React, { useState } from "react";
import "./CreateFormModal.css";

const CreateFormModal = ({ closeModal, isLightTheme, onCreateForm }) => {
  const [formName, setFormName] = useState("");

  const handleDone = () => {
    if (formName.trim()) {
      onCreateForm(formName.trim());
    }
    setFormName(""); // Clear the input field
    closeModal(); // Close the modal after creating the form
  };

  return (
    <div className={`modal-overlay ${isLightTheme ? "light" : "dark"}`}>
      <div className={`modal-container ${isLightTheme ? "light" : "dark"}`}>
        <h3 className="modal-title">Create New Form</h3>
        <input
          type="text"
          placeholder="Enter form name"
          value={formName}
          onChange={(e) => setFormName(e.target.value)}
          className="form-input"
        />
        <div className="modal-actions">
          <button onClick={handleDone} className="modal-done-button">
            Done
          </button>
          <button onClick={closeModal} className="modal-cancel-button">
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateFormModal;
