import "./Dashboard.css";
import axios from "axios";
import CreateFolderModal from "./CreateFolderModal";
import CreateFormModal from "./CreateFormModal";
import DeleteFolder from "./DeleteFolder";
import DeleteForm from "./DeleteForm";
import Settings from "./Settings";
import ShareModal from "./ShareModal";
import { useState, useEffect } from "react";

const Dashboard = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [folders, setFolders] = useState([]);
  const [forms, setForms] = useState([]);
  const [username, setUsername] = useState("");
  const [activeFolderIndex, setActiveFolderIndex] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isDeleteFormModalOpen, setIsDeleteFormModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);

  useEffect(() => {
    const fetchUsername = async () => {
      try {
        const token = localStorage.getItem("jwtToken");
        if (token) {
          const response = await axios.get("http://localhost:8000/username", {
            headers: { Authorization: `Bearer ${token}` },
          });
          setUsername(response.data?.username || "");
        }
      } catch (error) {
        console.error("Error fetching username:", error);
      }
    };
    fetchUsername();
  }, []);

  useEffect(() => {
    const fetchFolders = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/folders");
        setFolders(response.data);
      } catch (error) {
        console.error("Error fetching folders:", error);
      }
    };
    fetchFolders();
  }, []);

  useEffect(() => {
    const fetchForms = async () => {
      if (activeFolderIndex !== null) {
        const folderId = folders[activeFolderIndex]._id;
        try {
          const response = await axios.get(`http://localhost:8000/api/forms/${folderId}`);
          setForms(response.data);
        } catch (error) {
          console.error("Error fetching forms:", error);
        }
      }
    };
    fetchForms();
  }, [activeFolderIndex, folders]);

  const openModal = (type) => {
    setModalType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalType(null);
  };

  const handleCreateFolder = async (folderName) => {
    if (folderName.trim()) {
      try {
        const response = await axios.post("http://localhost:8000/api/folders", { name: folderName.trim() });
        setFolders((prevFolders) => [...prevFolders, response.data]);
      } catch (error) {
        if (error.response?.status === 409) {
          alert("Folder name must be unique.");
        } else {
          console.error("Error creating folder:", error);
          alert("Failed to create folder.");
        }
      }
    }
  };

  const handleCreateForm = async (formName) => {
    if (formName.trim()) {
      const folderId = folders[activeFolderIndex]._id;
      try {
        const response = await axios.post("http://localhost:8000/api/forms", {
          name: formName.trim(),
          folderId,
        });
        setForms((prevForms) => [...prevForms, response.data]);
      } catch (error) {
        console.error("Error creating form:", error);
        alert("Failed to create form.");
      }
    }
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <button onClick={() => openModal("create-folder")}>Create Folder</button>
        <button onClick={() => openModal("create-form")}>Create Form</button>
        <button onClick={() => openModal("settings")}>Settings</button>
        <button onClick={() => openModal("share")}>Share</button>
      </div>

      <div className="content">
        <h2>Welcome, {username}</h2>

        <div className="folder-list">
          {folders.map((folder, index) => (
            <div
              key={folder._id}
              className={`folder-item ${activeFolderIndex === index ? "active" : ""}`}
              onClick={() => setActiveFolderIndex(index)}
            >
              {folder.name}
            </div>
          ))}
        </div>

        <div className="form-list">
          {activeFolderIndex !== null &&
            forms.map((form) => <div key={form._id} className="form-item">{form.name}</div>)}
        </div>
      </div>

      {isModalOpen && modalType === "create-folder" && (
        <CreateFolderModal closeModal={closeModal} onCreate={handleCreateFolder} />
      )}

      {isModalOpen && modalType === "create-form" && (
        <CreateFormModal closeModal={closeModal} onCreate={handleCreateForm} />
      )}

      {isModalOpen && modalType === "settings" && <Settings closeModal={closeModal} />}
      {isModalOpen && modalType === "share" && <ShareModal closeModal={closeModal} />}
    </div>
  );
};

export default Dashboard;
