import React, { useState } from "react";
import "./Workspace.css";
import Workspacenavbar from "./Workspacenavbar";
import InputComp from "./Workflow-buttons/InputComp";
import ImageComp from "./Workflow-buttons/ImageComp";

const Workspace = () => {
  const [workflowItems, setWorkflowItems] = useState([]);
  const [showResponse, setShowResponse] = useState(false);

  // Handler to add components to the workflow list
  const handleAddToWorkflow = (component) => {
    setWorkflowItems((prevItems) => [...prevItems, component]);
  };

  // Handler to show the response
  const handleResponseClick = () => {
    setShowResponse(true);
  };

  return (
    <div className="workspace-container">
      {/* Include the Navbar */}
      <Workspacenavbar onResponseClick={handleResponseClick} />

      <div className="workspace">
        <div className="sidebar">
          <div className="section">
            <h3 className="section-title">Bubbles</h3>
            <div className="button-grid">
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<InputComp />)}
              >
                <img src="text.png" alt="Text" className="icon" /> Text
              </button>
              <button
                className="tool-button"
                onClick={() => handleAddToWorkflow(<ImageComp />)}
              >
                <img src="img.png" alt="Image" className="icon" /> Image
              </button>
            </div>
          </div>
        </div>
        <div className="main-panel">
          {showResponse ? (
            <div className="response-content">
              <h3>Response</h3>
              <p>This is the response content displayed after clicking "Response".</p>
            </div>
          ) : (
            <ul className="workflow-list">
              <div className="start-button">
                <img src="flag.png" alt="Start" className="icon" /> Start
              </div>
              {workflowItems.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default Workspace;
